import React from "react";
import axios from "axios";


export default function Comments(){
    axios
      .get("http://localhost:9292/comment")
      .then((res) => {
        console.log(res.data[0].email);
      })
      .then((info) => info);
    console.log(info);


    return(
        <div>
            <form>
                <textarea placeholder="What's your opinion?" input="text">

                </textarea>
                <div>
                    <button>
                        Submit
                    </button>
                </div>
            </form>
        </div>
    );
}
